content.output:
{{#each word in .}}
	<b>{{word.word}}</b>{{#if word.phonetic}}<i>{{word.phonetic}}</i>{{/if}}
	{{#each:word meanings in word.meanings}}
		<b>*{{meanings.partOfSpeech}}</b>
		{{#each:meanings definitions in meanings.definitions}}
			<i>*-{{definitions.definition}}</i>
		{{/each:meanings}}
	{{/each:word}}
{{/each}}
